import React, { useState } from "react";

import Card from "../UI/Card/Card";
import classes from "./Login.module.css";
import Button from "../UI/Button/Button";

const Login = (props) => {
  const [enteredCard, setEnteredCard] = useState("");
  const [cardIsValid, setCardIsValid] = useState();
  const [enteredPassword, setEnteredPassword] = useState("");
  const [passwordIsValid, setPasswordIsValid] = useState();
  const [formIsValid, setFormIsValid] = useState(false);

  const cardChangeHandler = (event) => {
    setEnteredCard(event.target.value);
    setFormIsValid(
      /^[a-zA-Z ]*$/.test(event.target.value) && enteredPassword.trim().length > 6
    );
  };

  const passwordChangeHandler = (event) => {
    setEnteredPassword(event.target.value);

    setFormIsValid(
      /[0-9\s]{13,19}/.test(event.target.value) && /^[a-zA-Z ]*$/.test(enteredCard)
    );
  };

  const validateEmailHandler = () => {
    setCardIsValid(/^[a-zA-Z ]*$/.test(enteredCard) );
  };

  const validatePasswordHandler = () => {
    setPasswordIsValid(/[0-9 ]{13,19}/.test(enteredPassword.trim()));
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(enteredCard, enteredPassword);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            cardIsValid === false ? classes.invalid : ""
          }`}
        >
          <label htmlFor="name">CARDHOLDER NAME</label>
          <input
            type="text"
            id="name"
            value={enteredCard}
            onChange={cardChangeHandler}
            onBlur={validateEmailHandler}
          />
          <div id="ename" className={classes.emsg}></div>
        </div>
        <div
          className={`${classes.control} ${
            passwordIsValid === false ? classes.invalid : ""
          }`}
        >
          <label htmlFor="cardNo">CARD NUMBER</label>
          <input
            type="text"
            pattern="[0-9\s]{13,19}"
            value={enteredPassword}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
          <div id="ecard" className={classes.emsg}></div>
        </div>
        <div className={classes.holder}>
          <div
            className={`${classes.control} ${
              passwordIsValid === false ? classes.invalid : ""
            }`}
          >
            <label htmlFor="expDate">EXP. DATE (MM/YY)</label>
            <div className={classes.subHolder}>
              <input
                type="number"
                className={classes.holder1}
                id="expDate"
                value={enteredPassword}
                onChange={passwordChangeHandler}
                onBlur={validatePasswordHandler}
              />
              <input
                type="number"
                className={classes.holder2}
                id="expDate"
                value={enteredPassword}
                onChange={passwordChangeHandler}
                onBlur={validatePasswordHandler}
              />
            </div>
            <div id="eexp" className={classes.emsg}></div>
          </div>
          <div
            className={`${classes.control} ${
              passwordIsValid === false ? classes.invalid : ""
            }`}
          >
            <label htmlFor="cvc">CVC</label>
            <input
              type="number"
              className={classes.holder3}
              id="cvc"
              value={enteredPassword}
              onChange={passwordChangeHandler}
              onBlur={validatePasswordHandler}
            />
            <div id="ecvc" className={classes.emsg}></div>
          </div>
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Confirm
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
